function [ fh ] = bsc_quickPlot(fg, color)
%
% inputs
% fg = fiber group of standard structure which is to be plotted
% color = uniform color (of format [x,y,z], where x y and z are all values 
% between 0 and 1 to be used for the lines of the plot, defaults to a
% random color
% 
% outputs: 
% fh = a figure handle for the figure object generated by this script

if notDefined('color')
    color=[rand(1),rand(1),rand(1)];
end

figure
hold on




for ifiblength= 1:length(fg.fibers)
    
    plot3(fg.fibers{ifiblength}(1,:),fg.fibers{ifiblength}(2,:),fg.fibers{ifiblength}(3,:),'Color',color);
    
end


title(strcat('plot of_', fg.name));
xlabel('X coordinates');
ylabel('Y coordinates');
zlabel('Z coordinates');

fh=gcf;
end

